PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "fluent" ("id" BLOB NOT NULL PRIMARY KEY, "name" TEXT NOT NULL, "batch" INTEGER NOT NULL, "createdAt" REAL , "updatedAt" REAL );
CREATE TABLE IF NOT EXISTS "User" ("id" BLOB NOT NULL PRIMARY KEY, "name" TEXT NOT NULL, "username" TEXT NOT NULL, "password" TEXT NOT NULL, CONSTRAINT "uq:User.username" UNIQUE ("username"));
CREATE TABLE IF NOT EXISTS "Acronym" ("id" INTEGER NOT NULL PRIMARY KEY, "short" TEXT NOT NULL, "long" TEXT NOT NULL, "userID" BLOB NOT NULL, CONSTRAINT "fk:Acronym.userID+User.id" FOREIGN KEY ("userID") REFERENCES "User" ("id"));
CREATE TABLE IF NOT EXISTS "Category" ("id" INTEGER NOT NULL PRIMARY KEY, "name" TEXT NOT NULL, "description" TEXT );
CREATE TABLE IF NOT EXISTS "Acronym_Category" ("id" BLOB NOT NULL PRIMARY KEY, "acronymID" INTEGER NOT NULL, "categoryID" INTEGER NOT NULL, CONSTRAINT "fk:Acronym_Category.acronymID+Acronym.id" FOREIGN KEY ("acronymID") REFERENCES "Acronym" ("id") ON DELETE CASCADE, CONSTRAINT "fk:Acronym_Category.categoryID+Category.id" FOREIGN KEY ("categoryID") REFERENCES "Category" ("id") ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS "Post" ("id" INTEGER NOT NULL PRIMARY KEY, "title" TEXT NOT NULL, "content" TEXT NOT NULL, "userID" BLOB NOT NULL);
CREATE TABLE IF NOT EXISTS "PostResponse" ("id" INTEGER NOT NULL PRIMARY KEY, "title" TEXT NOT NULL, "content" TEXT NOT NULL, "username" TEXT NOT NULL, "postID" INTEGER NOT NULL);
CREATE TABLE IF NOT EXISTS "Token" ("id" BLOB NOT NULL PRIMARY KEY, "token" TEXT NOT NULL, "userID" BLOB NOT NULL, CONSTRAINT "fk:Token.userID+User.id" FOREIGN KEY ("userID") REFERENCES "User" ("id"));
COMMIT;
